import React from 'react';
import cookie from 'react-cookies';
// import {Button} from 'react-materialize'

const {ConnectUser,getOnlineUsers,disconnectSocket,socket}=require('../socket');
// const {backendURL}=require('../url');


class Tictactoe extends React.Component{
    constructor(props){
        super(props);
        this.state={
            isOkay:false,
            users:[],
            ioId:''
        };

        // this.getUsers=this.getUsers.bind(this);
    }

    componentDidMount(){
        socket.on('OnlineUsers',data=>{this.setState({users:data.users,ioId:data.socket_id})});
        console.log(this.props.match.params);
        if(this.props.match.params.game==='tictactoe')
            this.setState({isOkay:true});
        ConnectUser();
        getOnlineUsers();
    }
    componentWillUnmount(){
        disconnectSocket();
    }

    render(){
        console.log(this.state);

        let connections;
        if(this.state.users.length===0)
            connections=<h4>"No one is online"</h4>;
        else{
            connections=this.state.user.filter(i=>{
                if(i.user_id!==this.state.ioId){
                    return(
                        <div style={{textAlign:'center'}}>
                            <h4>{i.name}</h4>
                            <button>Click To challeneg</button>
                        </div>
                    )
                }
            })
        }
        if(this.state.isOkay){
        return(
            <div style={{textAlign:'center'}}>
                {connections}
            </div>
        )
    }
    else{
        return(
            <div style={{textAlign:'center'}}>
                <h1>Work is in developement</h1>
            </div>
        )
    }
}

}

export default Tictactoe;
